import { currencyCodeSymbolMapping } from "./currencyCodeSymbolMapping";
/**
 *
 * @param code Code of the currency.
 * @returns true/false if the code is valid currency code.
 */
export const isValidCurrencyCode = (code) => Boolean(currencyCodeSymbolMapping[code]);
/**
 *
 * @param symbol Symbol of the currency.
 * @returns true/false if the currency symbol is valid.
 */
export const isValidCurrencySymbol = (symbol) => Boolean(Object.keys(currencyCodeSymbolMapping).find((key) => currencyCodeSymbolMapping[key] === symbol));
/**
 *
 * @param symbol Symbol of the currency. For Example USD.
 * @returns the code of the currency for that symbol
 */
export const getSymbolFromCode = (code) => currencyCodeSymbolMapping[code];
/**
 *
 * @param code Code of the currency. For Example $
 * @returns the symbol of the currency for that code
 */
export const getCodesFromSymbol = (symbol) => Object.keys(currencyCodeSymbolMapping).filter((key) => currencyCodeSymbolMapping[key] === symbol);
